package findAllAnagrams;

import java.util.*;

/**
 * Алгоритм получился не очень эффективным
 * Создаем хешМапу куда кладем в качестве ключей символы p.
 * Итерируемся по s с условием пока i меньше длины s - p.length, и сравнвиаем, если символ ссодержится среди ключей, значит это потенциальня комбинация.
 * Берем от этой итерации то количество символов, длина которой равно p, в массива чаров, сортировка и сравнение, если ок, сохраняем индекс итерации как вхождение.
 *
 */
public class Main {
    public static void main(String[] args) {
        String s = "";
        String p = "";
        System.out.println(findAnagrams(s, p));
    }

    public static List<Integer> findAnagrams(String s, String p) {
        List<Integer> resultList = new ArrayList<>();
        if(s.length()<p.length()) return resultList;
        Map<String, Integer> pMap = new HashMap<>();
        char[] pChar = p.toCharArray();
        Arrays.sort(pChar);
        for (int i = 0; i < pChar.length; i++) {
            pMap.put(String.valueOf(pChar[i]), 1);
        }
        char[] sChars = s.toCharArray();
        for (int i = 0; i <= sChars.length - pChar.length; i++) {
            String s1 = String.valueOf(sChars[i]);
            if (pMap.containsKey(s1)) {
                String substring = s.substring(i, i + p.length());
                char[] chars1 = substring.toCharArray();
                Arrays.sort(chars1);
                if (String.valueOf(pChar).equals(String.valueOf(chars1))) resultList.add(i);
            }
        }
        return resultList;
    }
}
